import { useState, useEffect, useCallback, useMemo } from "react"
import { Outlet } from "react-router-dom"
import { Circle, TrendingUp, Clock } from "lucide-react"
import Navbar from "./Navbar"
import { useAuth } from "../contexts/authContext"

const Layout = ({ children }) => {
  const { currentUser } = useAuth()
  const [tasks, setTasks] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  const fetchTasks = useCallback(async (forceRefresh = false) => {
    console.log('fetchTasks called - currentUser:', !!currentUser, 'forceRefresh:', forceRefresh)
    if (!currentUser && !forceRefresh) {
      console.log('Skipping fetch - no user and no force refresh')
      return
    }
    
    setLoading(true)
    setError(null)

    try {
      console.log('Fetching from API...')
      // Try to fetch from the API first
      const response = await fetch('http://localhost:5000/api/tasks')
      console.log('API response status:', response.status, response.ok)
      if (response.ok) {
        const data = await response.json()
        console.log('API data received:', data)
        // Handle different response formats
        const tasksArray = data.tasks || data.data || data || []
        console.log('Tasks array:', tasksArray.length, 'tasks')
        console.log('Setting tasks in Layout state...')
        setTasks(Array.isArray(tasksArray) ? tasksArray : [])
      } else {
        throw new Error('API not available')
      }
    } catch (err) {
      console.log('API error, using mock data:', err.message)
      // Fallback to mock data if API is not available
      const mockTasks = [
        {
          _id: '1',
          title: 'Welcome to TaskFast!',
          description: 'This is a sample task to get you started',
          priority: 'high',
          completed: false,
          createdAt: new Date().toISOString()
        },
        {
          _id: '2',
          title: 'Learn the basics',
          description: 'Explore the dashboard and features',
          priority: 'medium',
          completed: true,
          createdAt: new Date().toISOString()
        },
        {
          _id: '3',
          title: 'Create your first task',
          description: 'Add a new task using the Add button',
          priority: 'low',
          completed: false,
          createdAt: new Date().toISOString()
        }
      ]
      setTasks(mockTasks)
    } finally {
      setLoading(false)
    }
  }, [currentUser])

  useEffect(() => { 
    fetchTasks() 
  }, [fetchTasks])

  const refreshTasks = useCallback(() => {
    fetchTasks(true) // Force refresh regardless of currentUser
  }, [fetchTasks])

  // Debug: track when tasks change
  useEffect(() => {
    console.log('Layout tasks state changed:', tasks.length, 'tasks')
  }, [tasks])

  const contextValue = useMemo(() => ({
    tasks,
    refreshTasks
  }), [tasks, refreshTasks])

  const stats = useMemo(() => {
    const totalCount = tasks.length

    return {
      totalCount
    }
  }, [tasks])

  const StatCard = ({ title, value, icon }) => (
    <div className="p-2 sm:p-3 rounded-xl bg-white shadow-sm border border-purple-100 hover:shadow-md transition-all duration-300 hover:border-purple-200 group">
      <div className="flex items-center gap-2">
        <div className="p-1.5 rounded-lg bg-gradient-to-br from-fuchsia-500/10 to-purple-500/10 group-hover:from-fuchsia-500/20 group-hover:to-purple-500/20">
          {icon}
        </div>
        <div className="min-w-0">
          <p className="text-lg sm:text-xl font-bold bg-gradient-to-r from-fuchsia-500 to-purple-600 bg-clip-text text-transparent">
            {value}
          </p>
          <p className="text-xs text-gray-500 font-medium ">{title}</p>
        </div>
      </div>
    </div>
  )

  if (loading) return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
    </div>
  )

  if (error) return (
    <div className="min-h-screen bg-gray-50 p-6 flex items-center justify-center">
      <div className="bg-red-50 text-red-600 p-4 rounded-xl border border-red-100 max-w-md">
        <p className="font-medium mb-2">Error loading tasks</p>
        <p className="text-sm">{error}</p>
        <button
          onClick={fetchTasks}
          className="mt-4 px-4 py-2 bg-red-100 text-red-700 rounded-lg text-sm font-medium hover:bg-red-200 transition-colors"
        >
          Try Again
        </button>
      </div>
    </div>
  )

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="pt-16 p-3 sm:p-4 md:p-4 transition-all duration-300">
        <div className="grid grid-cols-1 xl:grid-cols-3 gap-4 sm:gap-6">
          <div className="xl:col-span-2 space-y-3 sm:space-y-4">
            {children || <Outlet context={{ tasks: tasks, refreshTasks: refreshTasks }} />}
          </div>

          <div className="xl:col-span-1 space-y-4 sm:space-y-6">
            <div className="bg-white rounded-xl p-4 sm:p-5 shadow-sm border border-purple-100">
              <h3 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 text-gray-800 flex items-center gap-2">
                <TrendingUp className="w-4 h-4 sm:w-5 sm:h-5 text-purple-500" />
                Task Statistics
              </h3>

              <div className="grid grid-cols-1 gap-3 sm:gap-4 mb-4 sm:mb-6">
                <StatCard 
                  title="Total Tasks" 
                  value={stats.totalCount} 
                  icon={<Circle className="w-3.5 h-3.5 sm:w-4 sm:h-4 text-purple-500" />} 
                />
              </div>
            </div>

            <div className="bg-white rounded-xl p-4 sm:p-5 shadow-sm border border-purple-100">
              <h3 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 text-gray-800 flex items-center gap-2">
                <Clock className="w-4 h-4 sm:w-5 sm:h-5 text-purple-500" />
                Recent Activity
              </h3>
              <div className="space-y-2 sm:space-y-3">
                {tasks.slice(0, 3).map((task) => (
                  <div
                    key={task._id || task.id}
                    className="flex items-center justify-between p-2 sm:p-3 hover:bg-purple-50/50 rounded-lg transition-colors duration-200 border border-transparent hover:border-purple-100"
                  >
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-700 break-words whitespace-normal">
                        {task.title}
                      </p>
                      <p className="text-xs text-gray-500 mt-0.5">
                        {task.createdAt ? new Date(task.createdAt).toLocaleDateString() : "No date"}
                      </p>
                    </div>
                  </div>
                ))}
                {tasks.length === 0 && (
                  <div className="text-center py-4 sm:py-6 px-2">
                    <div className="w-12 h-12 sm:w-16 sm:h-16 mx-auto mb-3 sm:mb-4 rounded-full bg-purple-100 flex items-center justify-center">
                      <Clock className="w-6 h-6 sm:w-8 sm:h-8 text-purple-500" />
                    </div>
                    <p className="text-sm text-gray-500">No recent activity</p>
                    <p className="text-xs text-gray-400 mt-1">Tasks will appear here</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Layout
