import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import withDragAndDrop from 'react-big-calendar/lib/addons/dragAndDrop';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'react-big-calendar/lib/addons/dragAndDrop/styles.css';
import { useAuth } from '../contexts/authContext';
import axios from 'axios';
import TaskModal from './AddTask';

const localizer = momentLocalizer(moment);
const DragAndDropCalendar = withDragAndDrop(Calendar);

const TaskCalendar = () => {
  const { currentUser } = useAuth();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [taskToEdit, setTaskToEdit] = useState(null);

  // Fetch tasks and convert them to calendar events
  const fetchTasksForCalendar = async () => {
    try {
      if (!currentUser?.email) {
        setLoading(false);
        return;
      }

      const response = await axios.get(`http://localhost:5000/api/tasks?userEmail=${currentUser.email}`);
      const tasks = response.data.tasks || response.data;

      // Convert tasks to calendar events
      const calendarEvents = tasks.map(task => ({
        id: task._id,
        title: task.title || task.task,
        start: task.dueDate ? new Date(task.dueDate) : new Date(),
        end: task.dueDate ? new Date(task.dueDate) : new Date(),
        resource: task,
        completed: task.completed
      }));

      setEvents(calendarEvents);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching tasks for calendar:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasksForCalendar();
  }, [currentUser]);

  // Custom event style getter
  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad';
    
    if (event.completed) {
      backgroundColor = '#28a745'; // Green for completed tasks
    } else if (event.resource.priority === 'high') {
      backgroundColor = '#dc3545'; // Red for high priority
    } else if (event.resource.priority === 'medium') {
      backgroundColor = '#ffc107'; // Yellow for medium priority
    }

    return {
      style: {
        backgroundColor,
        borderRadius: '5px',
        opacity: 0.8,
        color: 'white',
        border: '0px',
        display: 'block'
      }
    };
  };

  // Handle event selection
  const handleSelectEvent = (event) => {
    setTaskToEdit(event.resource);
    setIsModalOpen(true);
  };

  // Handle slot selection (for creating new tasks)
  const handleSelectSlot = ({ start, end }) => {
    setSelectedDate(moment(start).format('YYYY-MM-DD'));
    setTaskToEdit(null);
    setIsModalOpen(true);
  };

  // Handle task save from modal
  const handleTaskSave = () => {
    fetchTasksForCalendar(); // Refresh calendar data
    setIsModalOpen(false);
    setTaskToEdit(null);
    setSelectedDate(null);
  };

  // Handle drag and drop events
  const handleEventDrop = async ({ event, start, end }) => {
    try {
      const updatedTask = {
        ...event.resource,
        dueDate: moment(start).format('YYYY-MM-DD'),
        userEmail: currentUser?.email
      };

      const response = await axios.put(`http://localhost:5000/api/tasks/${event.id}`, updatedTask);
      
      if (response.data.success) {
        // Update the event in local state
        setEvents(prevEvents => 
          prevEvents.map(evt => 
            evt.id === event.id 
              ? { ...evt, start: new Date(start), end: new Date(start) }
              : evt
          )
        );
        console.log('Task due date updated successfully');
      }
    } catch (error) {
      console.error('Error updating task due date:', error);
      alert('Failed to update task due date. Please try again.');
      // Refresh calendar to revert changes
      fetchTasksForCalendar();
    }
  };

  // Handle event resize (if user drags to extend the duration)
  const handleEventResize = async ({ event, start, end }) => {
    // For tasks, we'll just update the due date to the end date
    await handleEventDrop({ event, start: end, end });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading calendar...</div>
      </div>
    );
  }

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Task Calendar</h2>
      <p className="text-sm text-gray-600 mb-4">
        ðŸ’¡ <strong>Tip:</strong> Drag tasks to different dates to update their due dates, or click to edit them.
      </p>
      <div style={{ height: '600px' }}>
        <DragAndDropCalendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: '100%' }}
          eventPropGetter={eventStyleGetter}
          onSelectEvent={handleSelectEvent}
          onSelectSlot={handleSelectSlot}
          onEventDrop={handleEventDrop}
          onEventResize={handleEventResize}
          selectable
          resizable
          popup
          dragFromOutsideItem={() => ({})}
          views={['month', 'week', 'day', 'agenda']}
          defaultView="month"
          step={60}
          showMultiDayTimes
          messages={{
            next: 'Next',
            previous: 'Previous',
            today: 'Today',
            month: 'Month',
            week: 'Week',
            day: 'Day',
            agenda: 'Agenda',
            date: 'Date',
            time: 'Time',
            event: 'Task',
            noEventsInRange: 'No tasks in this date range.',
            showMore: total => `+${total} more`
          }}
        />
      </div>
      
      {/* Legend */}
      <div className="mt-4 flex flex-wrap gap-4 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-green-500 rounded"></div>
          <span>Completed Tasks</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-red-500 rounded"></div>
          <span>High Priority</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-yellow-500 rounded"></div>
          <span>Medium Priority</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-blue-500 rounded"></div>
          <span>Normal Priority</span>
        </div>
      </div>

      {/* Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setTaskToEdit(null);
          setSelectedDate(null);
        }}
        taskToEdit={taskToEdit ? { ...taskToEdit, dueDate: selectedDate || taskToEdit.dueDate } : { dueDate: selectedDate }}
        onSave={handleTaskSave}
      />
    </div>
  );
};

export default TaskCalendar;
