import React, { useState, useEffect, useCallback, useMemo } from "react"
import { Outlet } from "react-router-dom"
import Navbar from "./Navbar"
import { useAuth } from "../contexts/authContext"

const Layout = ({ children }) => {
  const { currentUser } = useAuth()
  const [tasks, setTasks] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  const fetchTasks = useCallback(async (forceRefresh = false) => {
    console.log('🔄 Layout: fetchTasks called - forceRefresh:', forceRefresh)
    
    setLoading(true)
    setError(null)

    try {
      console.log('🌐 Layout: Fetching from API...')
      const response = await fetch('http://localhost:5000/api/tasks')
      if (response.ok) {
        const data = await response.json()
        const tasksArray = data.tasks || data.data || data || []
        console.log('✅ Layout: API returned', tasksArray.length, 'tasks - updating state')
        setTasks(Array.isArray(tasksArray) ? tasksArray : [])
      } else {
        throw new Error('API not available')
      }
    } catch (err) {
      console.log('❌ Layout: API error, using mock data:', err.message)
      // Fallback to mock data if API is not available
      const mockTasks = [
        {
          _id: '1',
          title: 'Welcome to TaskFast!',
          description: 'This is a sample task to get you started',
          priority: 'high',
          completed: false,
          createdAt: new Date().toISOString()
        },
        {
          _id: '2',
          title: 'Learn the basics',
          description: 'Explore the dashboard and features',
          priority: 'medium',
          completed: true,
          createdAt: new Date().toISOString()
        },
        {
          _id: '3',
          title: 'Create your first task',
          description: 'Add a new task using the Add button',
          priority: 'low',
          completed: false,
          createdAt: new Date().toISOString()
        }
      ]
      setTasks(mockTasks)
    } finally {
      setLoading(false)
    }
  }, []) // Temporarily remove currentUser dependency

  useEffect(() => { 
    console.log('useEffect running, calling fetchTasks...')
    fetchTasks() 
  }, [fetchTasks])

  const refreshTasks = useCallback(() => {
    console.log('🔄 Layout: refreshTasks called - forcing refresh...')
    fetchTasks(true) // Force refresh regardless of currentUser
  }, [fetchTasks])

  // Debug: track when tasks change
  useEffect(() => {
    console.log('Layout tasks state changed:', tasks.length, 'tasks')
  }, [tasks])

  const contextValue = useMemo(() => ({
    tasks,
    refreshTasks
  }), [tasks, refreshTasks])

  if (loading) return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
    </div>
  )

  if (error) return (
    <div className="min-h-screen bg-gray-50 p-6 flex items-center justify-center">
      <div className="bg-red-50 text-red-600 p-4 rounded-xl border border-red-100 max-w-md">
        <p className="font-medium mb-2">Error loading tasks</p>
        <p className="text-sm">{error}</p>
        <button
          onClick={fetchTasks}
          className="mt-4 px-4 py-2 bg-red-100 text-red-700 rounded-lg text-sm font-medium hover:bg-red-200 transition-colors"
        >
          Try Again
        </button>
      </div>
    </div>
  )

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navbar hover trigger area at top of screen */}
      <div className="fixed top-0 left-0 right-0 h-4 bg-transparent z-40 hover:bg-gray-100/10"></div>
      
      <Navbar />

      <div className="pt-8 p-3 sm:p-4 md:p-4 transition-all duration-300">
        {/* Full width layout - statistics moved to Dashboard */}
        <div className="w-full">
          {children || <Outlet context={contextValue} />}
        </div>
      </div>
    </div>
  )
}

export default Layout
