import React, { useState } from 'react'
import { Navigate, Link } from 'react-router-dom'
import { doSignInWithEmailAndPassword } from '../../../firebase/auth'
import { LocalAuth } from '../../../firebase/localAuth'
import { useAuth } from '../../../contexts/authContext'

const Login = () => {
    const { userLoggedIn, useLocalAuth, setCurrentUser, setUserLoggedIn } = useAuth()

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [isSigningIn, setIsSigningIn] = useState(false)
    const [errorMessage, setErrorMessage] = useState('')

    const onSubmit = async (e) => {
        e.preventDefault()
        if(!isSigningIn) {
            setIsSigningIn(true)
            setErrorMessage('')
            
            try {
                // Try local auth first for faster response
                try {
                    const user = LocalAuth.login(email, password)
                    setCurrentUser(user)
                    setUserLoggedIn(true)
                    return; // Success with local auth
                } catch (localError) {
                    // If local auth fails and we're not using local auth fallback, try Firebase
                    if (!useLocalAuth) {
                        try {
                            await doSignInWithEmailAndPassword(email, password)
                            return; // Success with Firebase
                        } catch (firebaseError) {
                            // If Firebase also fails, show the local auth error
                            throw localError;
                        }
                    } else {
                        // We're using local auth, so throw the local error
                        throw localError;
                    }
                }
            } catch (error) {
                console.error("Login error:", error)
                setErrorMessage(error.message || 'Login failed. Please check your credentials.')
                setIsSigningIn(false)
            }
        }
    }

    

    return (
        <div>
            {userLoggedIn && (<Navigate to={'/dashboard'} replace={true} />)}

            <main className="w-full h-screen flex self-center place-content-center place-items-center">
                <div className="w-96 text-gray-600 space-y-5 p-4 shadow-xl border rounded-xl">
                    <div className="text-center">
                        <div className="mt-2">
                            <h3 className="text-gray-800 text-xl font-semibold sm:text-2xl">Welcome Back</h3>
                        </div>
                    </div>
                    <form
                        onSubmit={onSubmit}
                        className="space-y-5"
                    >
                        <div>
                            <label className="text-sm text-gray-600 font-bold">
                                Email
                            </label>
                            <input
                                type="email"
                                autoComplete='email'
                                required
                                value={email} onChange={(e) => { setEmail(e.target.value) }}
                                className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg transition duration-300"
                            />
                        </div>


                        <div>
                            <label className="text-sm text-gray-600 font-bold">
                                Password
                            </label>
                            <input
                                type="password"
                                autoComplete='current-password'
                                required
                                value={password} onChange={(e) => { setPassword(e.target.value) }}
                                className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg transition duration-300"
                            />
                        </div>

                        {errorMessage && (
                            <span className='text-red-600 font-bold'>{errorMessage}</span>
                        )}

                        <button
                            type="submit"
                            disabled={isSigningIn}
                            className={`w-full px-4 py-2 text-white font-medium rounded-lg ${isSigningIn ? 'bg-gray-300 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700 hover:shadow-xl transition duration-300'}`}
                        >
                            {isSigningIn ? 'Signing In...' : 'Sign In'}
                        </button>
                    </form>
                    <p className="text-center text-sm">Don't have an account? <Link to={'/register'} className="hover:underline font-bold">Sign up</Link></p>
                </div>
            </main>
        </div>
    )
}

export default Login
